import numpy as npimport pandas as pdimport requestsimport matplotlib.pyplot as pltimport jsonurl = "https://everyearthquake.p.rapidapi.com/earthquakes"querystring = {"start":"1","count":"1000","type":"earthquake"}headers = {    "X-RapidAPI-Key": "38e70b15c0msh38290bbd827107cp13b4b1jsna1bfa233d75d",    "X-RapidAPI-Host": "everyearthquake.p.rapidapi.com"}    def api_caller(url,date,headers):    r = requests.request("GET", url, headers=headers, params=date)    data = json.loads(r.text)    df_temp = pd.json_normalize(data)    return(df_temp)def data_flattener(df_temp):    df_temp.columns    df_t=pd.json_normalize(df_temp['data'])    df_t.head()    df0=pd.json_normalize(df_t[0])    for i in df_t.columns:        df1=pd.json_normalize(df_t[i])        df0=df0.append(df1)    #     df0.drop(0,inplace=True)    return(df0)df_temp= api_caller(url,querystring,headers)df_temp.head()df=data_flattener(df_temp)df.reset_index(inplace=True);df.head()df.to_csv('earthquake_data.csv',index=False)